from flask import Flask,render_template,request,session,redirect,url_for
import config
import json
from finalAPI import *
app = Flask(__name__)
app.config.from_object(config)



# 默认初始路径，跳转到登陆页面
@app.route('/base')
def index():
    infomation = allCollectionStats()
    weiboCount = infomation['weiboCount']
    networkCount = infomation['networkCount']
    wordsCount = infomation['preferenceCount']
    relationShipCount = infomation['relationShipCount']
    print('weibo" % s , network: %s , relationShip : %s, words , % s'%(weiboCount,networkCount,relationShipCount,wordsCount))

    info = {
        'weibo' : weiboCount,
        'netWork' : networkCount,
        'relationShip' : relationShipCount,
        'words' : wordsCount
    }

    print(allCollectionStats())
    return render_template('index1.html',**info)

@app.route('/crawler',methods=['GET','POST'])
def crawler():
    if request.method == 'GET':
        return render_template('index2.html')
    else:
        weiboName = request.form.get('weiboName')
        print('需要爬取的微博用户id为：%s'%weiboName)
        info = json.dumps(getWeiboByName(weiboName),sort_keys=True,indent=2)
        return render_template('index2.html',info = info)

@app.route('/usernet',methods=['GET','POST'])
def usernet():
    if request.method == 'GET':
        return render_template('index3.html')
    else:
        weiboName = request.form.get('weiboName')

        infomation = getOneStatistics(weiboName)

        keyWords = infomation['keyWords']
        preference = infomation['preference']
        friends = infomation['friends']
        network = infomation['network']

        ODC = network['ODC']
        BC = network['BC']
        CC = network['CC']
        diameter = network['diameter']

        info = {
            'keyWords' : keyWords,
            'preference' : preference,
            'friends' : friends,
            'ODC' : ODC,
            'BC' : BC,
            'CC' : CC,
            'diameter' : diameter
        }


        return render_template('index3.html',**info)

